@media (min-width: 1200px) {
.container {
width: 1170px;
}
}

@media (min-width: 992px) {
.container {
width: 970px;
}
}

@media (min-width: 768px) {
.container {
width: 750px;
}
}

.container {
padding-right: 15px;
padding-left: 15px;
margin-right: auto;
margin-left: auto;
}

.row {
margin-right: -15px;
margin-left: -15px;
}

.calculator-section .r_block {
border: 3px solid #fff000;
border-radius: 30px;
max-width: 500px;
width: 100%;
/* margin: 0 auto; */
padding: 20px;
margin-top: 25px;
margin-left: auto;
margin-right: auto;
}

.calculator-section .r_block .oc_line {
font-size: 18px;
text-transform: uppercase;
display: table;
padding-bottom: 10px;
font-weight: bold;
}

.calculator-section .r_block .oc_line span {
vertical-align: middle;
display: table-cell;
padding-right: 50px;
}

.calculator-section .r_block .oc_line span.inp {
padding-right: 20px;
padding-left: 20px;
}

.calculator-section input[type="checkbox"].need_otliv {
width: 81px;
height: 41px;
-webkit-appearance: none;
-moz-appearance: none;
/* background: #c6c6c6; */
background: url('data:image/png;base64,') no-repeat center center;
outline: none;
border-radius: 50px;
/* box-shadow: inset 0 0 5px rgba(0,0,0, .2); */
transition: 0.5s;
position: relative;
vertical-align: middle;
display: table-cell;
}

.calculator-section input[type="checkbox"].need_otliv::before {
content: '';
position: absolute;
width: 44px;
height: 44px;
top: -1px;
left: -1px;
/* background: #fff; */
background: url('data:image/png;base64,') no-repeat center center;
transform: scale(1.1);
transition: 0.5s;
background-size: 90%;
}

.calculator-section .r_block .oc_pr_block {
font-size: 18px;
text-transform: uppercase;
border-top: 2px solid #fff000;
border-bottom: 2px solid #fff000;
padding-top: 20px;
padding-bottom: 20px;
margin-top: 10px;
font-weight: bold;
}

.calculator-section .r_block .oc_pr_block .bl_left span {
color: #e51d65;
font-size: 55px;
}

.calculator-section .b_btn {
font-size: 20px;
width: 350px;
padding-top: 20px;
padding-bottom: 20px;
}

.b_btn {
background: #ed226b;
/* height: 66px; */
width: 250px;
font-weight: bold;
color: #fff;
font-size: 30px;
text-align: center;
/* padding-top: 13px; */
cursor: pointer;
/* margin-top: 30px; */
padding-top: 18px;
padding-bottom: 17px;
border-radius: 36px;
text-transform: uppercase;
margin: 0 auto;
display: block;
}

.calculator-section .type-container {
display: flex;
justify-content: space-around;
align-items: center;
margin: 15px 0;
}

.calculator-section .type-container .choose-type-window {
font-size: 17px;
text-transform: uppercase;
font-weight: bold;
}

.calculator-section .window-menu {
display: flex;
align-items: center;
}

.calculator-section .window-menu .type-window {
margin: 0 10px;
}

.calculator-section .window-menu .type-window:first-child {
margin-left: 0;
}

.calculator-section .window-menu .type-window:last-child {
margin-right: 0;
}

.calculator-section .window-menu .type-window img {
width: 45px;
height: 40px;
object-fit: cover;
}

.calculator-section h1 {
text-align: center;
color: #333;
font-size: 40px;
margin: 40px auto;
text-transform: uppercase;
font-weight: bold;
}